// Squad.js
import React, { useState, useEffect } from 'react';

const Squad = () => {
  const [squad, setSquad] = useState([]);
  const [budget, setBudget] = useState(500); // Budget iniziale

  // Esempio di giocatori (sostituisci con dati reali)
  const samplePlayers = [
    { id: 1, name: 'Lautaro Martinez', team: 'Inter', role: 'FWD', price: 80 },
    { id: 2, name: 'Mike Maignan', team: 'Milan', role: 'GK', price: 25 },
    { id: 3, name: 'Theo Hernandez', team: 'Milan', role: 'DEF', price: 45 },
  ];

  const addPlayer = (player) => {
    if (budget >= player.price) {
      setSquad([...squad, player]);
      setBudget(budget - player.price);
    }
  };

  const removePlayer = (playerId) => {
    const player = squad.find(p => p.id === playerId);
    setSquad(squad.filter(p => p.id !== playerId));
    setBudget(budget + player.price);
  };

  return (
    <div className="squad-container">
      <h1>La Mia Squadra</h1>
      
      <div className="squad-info">
        <p>Budget rimanente: <strong>€{budget}M</strong></p>
        <p>Giocatori in rosa: {squad.length}/25</p>
      </div>

      <div className="squad-players">
        <h2>Formazione</h2>
        {squad.length === 0 ? (
          <p>Nessun giocatore in squadra</p>
        ) : (
          <ul>
            {squad.map(player => (
              <li key={player.id}>
                {player.name} ({player.team}) - €{player.price}M
                <button onClick={() => removePlayer(player.id)}>Rimuovi</button>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="available-players">
        <h2>Giocatori Disponibili</h2>
        <ul>
          {samplePlayers.map(player => (
            <li key={player.id}>
              {player.name} ({player.team}) - €{player.price}M
              <button 
                onClick={() => addPlayer(player)}
                disabled={budget < player.price || squad.some(p => p.id === player.id)}
              >
                Aggiungi
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Squad;
