import React, { useState, useEffect } from 'react';
import './Squad.css';

const Squad = () => {
  const [squad, setSquad] = useState([]);
  const [playerInput, setPlayerInput] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  const roleLimits = { GK: 3, DEF: 8, MID: 8, FWD: 6 };

  const countByRole = (role) => squad.filter(p => p.role === role).length;
  const canAddPlayer = (player) => countByRole(player.role) < roleLimits[player.role];

  // --- Ricerca giocatori --- 
  const searchPlayers = async (query) => {
    if (!query || query.length < 2) {
      setSearchResults([]);
      return;
    }
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:8000/api/players/search?name=${encodeURIComponent(query)}`);
      const data = await res.json();
      if (data.status === 'success') {
        setSearchResults(data.players);
      } else {
        setSearchResults([]);
      }
    } catch (err) {
      console.error(err);
      setSearchResults([]);
    } finally {
      setLoading(false);
    }
  };

  const addPlayer = (player) => {
    if (!player || !canAddPlayer(player)) return;
    const newPlayer = { ...player, id: Date.now() };
    setSquad([...squad, newPlayer]);
    setPlayerInput('');
    setSearchResults([]);
    setSelectedPlayer(null);
  };

  const addFirstPlayer = () => {
    if (searchResults.length > 0) addPlayer(searchResults[0]);
    else if (selectedPlayer) addPlayer(selectedPlayer);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addFirstPlayer();
    }
  };

  const selectPlayer = (player) => setSelectedPlayer(player);

  useEffect(() => {
    const delay = setTimeout(() => {
      if (playerInput.length >= 2) searchPlayers(playerInput);
      else setSearchResults([]);
    }, 300);
    return () => clearTimeout(delay);
  }, [playerInput]);

  const getRoleName = (role) => ({
    GK: 'portieri',
    DEF: 'difensori',
    MID: 'centrocampisti',
    FWD: 'attaccanti'
  }[role] || role);

  const squadByRole = {
    GK: squad.filter(p => p.role === 'GK'),
    DEF: squad.filter(p => p.role === 'DEF'),
    MID: squad.filter(p => p.role === 'MID'),
    FWD: squad.filter(p => p.role === 'FWD')
  };

  return (
    <div className="squad-container">
      <h1>🏆 La Mia Squadra Fantacalcio</h1>

      {/* Input giocatore */}
      <div className="player-search-section">
        <input
          type="text"
          placeholder="Es: Lautaro Martinez..."
          value={playerInput}
          onChange={e => setPlayerInput(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button onClick={addFirstPlayer} disabled={searchResults.length === 0 && !selectedPlayer}>⏎ INVIO</button>

        {loading && <div>🔍 Cercando...</div>}

        {searchResults.length > 0 && !selectedPlayer && (
          <div className="search-results">
            {searchResults.map(player => (
              <div
                key={player.id}
                className={`player-card ${!canAddPlayer(player) ? 'disabled' : ''}`}
                onClick={() => canAddPlayer(player) && selectPlayer(player)}
              >
                <h5>{player.name}</h5>
                <p>{player.team} • {player.role}</p>
                <p>€{player.price}M</p>
              </div>
            ))}
          </div>
        )}

        {selectedPlayer && (
          <div className="selected-player">
            <h4>🎯 Giocatore selezionato</h4>
            <p>{selectedPlayer.name} • {selectedPlayer.team} • {selectedPlayer.role}</p>
            <button onClick={() => addPlayer(selectedPlayer)}>✅ Conferma Aggiunta</button>
            <button onClick={() => setSelectedPlayer(null)}>❌ Annulla</button>
          </div>
        )}
      </div>

      {/* Rosa completa */}
      <div className="squad-roster">
        {Object.entries(squadByRole).map(([role, players]) => (
          <div key={role} className="role-section">
            <h3>{getRoleName(role)} ({players.length}/{roleLimits[role]})</h3>
            <div className="players-grid">
              {players.length === 0 ? <p>Nessun giocatore</p> :
                players.map(player => (
                  <div key={player.id} className="squad-player-card">
                    <div className="player-header">
                      <h4>{player.name}</h4>
                      <p>{player.team} • {player.role}</p>
                      <p>€{player.price}M</p>
                    </div>
                  </div>
                ))
              }
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Squad;
