import React, { useState, useContext } from "react";
import { AuthContext } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState(""); // puoi ignorare la password se vuoi
  const [error, setError] = useState("");

  // ========================
  // ðŸ”¹ FUNZIONI JSON SIMULATO
  // ========================
  const getUsers = () => {
    const stored = localStorage.getItem("fantacalcio_users");
    return stored ? JSON.parse(stored) : { users: [] };
  };

  const saveUsers = (data) => {
    localStorage.setItem("fantacalcio_users", JSON.stringify(data));
  };

  const createOrGetUser = (email) => {
    const data = getUsers();
    let user = data.users.find(u => u.email === email);
    if (!user) {
      user = { email, squad: [] };
      data.users.push(user);
      saveUsers(data);
    }
    return user;
  };

  // ========================
  // ðŸ”¹ HANDLE LOGIN
  // ========================
  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");

    if (!email) {
      setError("Inserisci la email");
      return;
    }

    try {
      const userData = createOrGetUser(email);
      login(userData); // aggiorna AuthContext
      navigate("/squad"); // reindirizza a Squad
    } catch (err) {
      console.error(err);
      setError("Errore interno");
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Welcome back</h2>
        <form className="auth-form" onSubmit={handleSubmit}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {error && <p style={{ color: "red", marginBottom: "10px" }}>{error}</p>}
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
