import React, { useState, useContext } from "react";
import { AuthContext } from "../contexts/AuthContext";
import { useNavigate } from "react-router-dom";
import "./Register.css";

const Register = () => {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  // ========================
  // üîπ FUNZIONI JSON SIMULATO
  // ========================
  const getUsers = () => {
    const stored = localStorage.getItem("fantacalcio_users");
    return stored ? JSON.parse(stored) : { users: [] };
  };

  const saveUsers = (data) => {
    localStorage.setItem("fantacalcio_users", JSON.stringify(data));
  };

  const createUser = (email) => {
    const data = getUsers();
    const exists = data.users.some(u => u.email === email);
    if (exists) return null; // utente gi√† registrato

    const newUser = { email, squad: [] };
    data.users.push(newUser);
    saveUsers(data);
    return newUser;
  };

  // ========================
  // üîπ HANDLE REGISTER
  // ========================
  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");

    if (password.length < 8) {
      setError("La password deve essere di almeno 8 caratteri");
      return;
    }
    if (password.length > 50) {
      setError("La password non pu√≤ superare i 50 caratteri");
      return;
    }

    setIsLoading(true);

    try {
      const newUser = createUser(email);
      if (!newUser) {
        setError("Email gi√† registrata");
        setIsLoading(false);
        return;
      }

      // Login automatico
      login(newUser);
      navigate("/squad");
    } catch (err) {
      console.error(err);
      setError("Errore interno");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="register-container">
      <div className="register-card">
        <h2>Welcome to FANTACALCIO AI</h2>
        <form className="auth-form" onSubmit={handleSubmit}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            disabled={isLoading}
            required
          />
          <input
            type="password"
            placeholder="Password (minimo 8 caratteri)"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            disabled={isLoading}
            required
          />
          {error && <p style={{ color: "red", marginBottom: "10px" }}>{error}</p>}
          <button type="submit" disabled={isLoading}>
            {isLoading ? "Registrazione in corso..." : "Registrati"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
