import React, { useState, useEffect } from 'react';
import './Squad.css';

const Squad = () => {
  const [squad, setSquad] = useState([]);
  const [playerInput, setPlayerInput] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [playerStats, setPlayerStats] = useState({});
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  // Limiti per ruolo
  const roleLimits = {
    'GK': 3,
    'DEF': 8,
    'MID': 8,
    'FWD': 6
  };

  const countByRole = (role) => squad.filter(p => p.role === role).length;

  const canAddPlayer = (player) => countByRole(player.role) < roleLimits[player.role];

  const searchPlayers = async (query) => {
    if (!query || query.length < 3) {
      setSearchResults([]);
      return;
    }
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/api/players/search?name=${encodeURIComponent(query)}`);
      const data = await response.json();
      setSearchResults(data.status === 'success' ? data.players : []);
    } catch (err) {
      console.error(err);
      setSearchResults([]);
    } finally {
      setLoading(false);
    }
  };

  const addPlayer = async (player) => {
    if (!player) return;
    if (!canAddPlayer(player)) {
      alert(`Non puoi aggiungere pi√π ${getRoleName(player.role)}! Limite: ${roleLimits[player.role]}`);
      return;
    }
    try {
      const response = await fetch('http://localhost:8000/api/players/add-to-squad', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: player.name,
          team: player.team,
          role: player.role,
          price: player.price,
          stats: player.stats,
          fantacalcio_data: player.fantacalcio_data
        })
      });
      const result = await response.json();
      if (result.status === 'success') {
        const newPlayer = { id: result.player_id || Date.now(), ...player };
        setSquad([...squad, newPlayer]);
        setPlayerInput('');
        setSearchResults([]);
        setSelectedPlayer(null);
        loadPlayerStats(newPlayer);
      }
    } catch (err) {
      console.error(err);
      alert('‚ùå Errore nell\'aggiungere il giocatore');
    }
  };

  const addFirstPlayer = () => {
    if (searchResults.length > 0) addPlayer(searchResults[0]);
    else if (selectedPlayer) addPlayer(selectedPlayer);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addFirstPlayer();
    }
  };

  const selectPlayer = (player) => setSelectedPlayer(player);

  const loadPlayerStats = async (player) => {
    try {
      const response = await fetch(`http://localhost:8000/api/players/${player.id}/stats`);
      const data = await response.json();
      if (data.status === 'success') {
        setPlayerStats(prev => ({ ...prev, [player.id]: data.stats }));
      }
    } catch (err) {
      console.error(err);
    }
  };

  const removePlayer = async (playerId) => {
    const player = squad.find(p => p.id === playerId);
    try {
      await fetch(`http://localhost:8000/api/players/${playerId}/remove-from-squad`, { method: 'DELETE' });
      setSquad(squad.filter(p => p.id !== playerId));
      setPlayerStats(prev => { const newStats = { ...prev }; delete newStats[playerId]; return newStats; });
    } catch (err) {
      console.error(err);
      alert('‚ùå Errore nella rimozione del giocatore');
    }
  };

  const squadByRole = {
    'GK': squad.filter(p => p.role === 'GK'),
    'DEF': squad.filter(p => p.role === 'DEF'),
    'MID': squad.filter(p => p.role === 'MID'),
    'FWD': squad.filter(p => p.role === 'FWD')
  };

  const getRoleName = (role) => ({
    'GK': 'portieri',
    'DEF': 'difensori',
    'MID': 'centrocampisti',
    'FWD': 'attaccanti'
  }[role] || role);

  useEffect(() => {
    const delay = setTimeout(() => {
      if (playerInput.length >= 3) {
        searchPlayers(playerInput);
        setSelectedPlayer(null);
      } else {
        setSearchResults([]);
        setSelectedPlayer(null);
      }
    }, 500);
    return () => clearTimeout(delay);
  }, [playerInput]);

  return (
    <div className="squad-container">
      <h1>üèÜ La Mia Squadra Fantacalcio</h1>

      {/* Riepilogo ruoli */}
      <div className="squad-summary">
        {Object.entries(roleLimits).map(([role, limit]) => {
          const current = countByRole(role);
          return (
            <div key={role} className="role-summary">
              <h4>{getRoleName(role)} ({current}/{limit})</h4>
              <div className="role-progress-bar">
                <div className="role-progress-fill" style={{ width: `${(current / limit) * 100}%` }}></div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Ricerca giocatori */}
      <div className="player-search-section">
        <input
          type="text"
          placeholder="Es: Lautaro Martinez..."
          value={playerInput}
          onChange={(e) => setPlayerInput(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button onClick={addFirstPlayer} disabled={searchResults.length === 0 && !selectedPlayer}>‚èé INVIO</button>

        {loading && <div className="loading-spinner">üîç Cercando...</div>}

        {searchResults.length > 0 && !selectedPlayer && (
          <div className="search-results">
            {searchResults.map(player => (
              <div
                key={player.id}
                className={`player-card ${!canAddPlayer(player) ? 'disabled' : ''}`}
                onClick={() => canAddPlayer(player) && selectPlayer(player)}
              >
                <h5>{player.name}</h5>
                <p>{player.team} ‚Ä¢ {player.role}</p>
                <p>‚Ç¨{player.price}M</p>
              </div>
            ))}
          </div>
        )}

        {selectedPlayer && (
          <div className="selected-player">
            <h4>üéØ Giocatore selezionato</h4>
            <p>{selectedPlayer.name} ‚Ä¢ {selectedPlayer.team} ‚Ä¢ {selectedPlayer.role}</p>
            <button onClick={() => addPlayer(selectedPlayer)}>‚úÖ Conferma Aggiunta</button>
            <button onClick={() => setSelectedPlayer(null)}>‚ùå Annulla</button>
          </div>
        )}
      </div>

      {/* Rosa completa */}
      <div className="squad-roster">
        {Object.entries(squadByRole).map(([role, players]) => (
          <div key={role} className="role-section">
            <h3>{getRoleName(role)} ({players.length}/{roleLimits[role]})</h3>
            <div className="players-grid">
              {players.length === 0 ? <p>Nessun giocatore</p> :
                players.map(player => (
                  <div
                    key={player.id}
                    className={`squad-player-card ${players.length >= roleLimits[role] ? 'full-role' : ''}`}
                  >
                    <div className="player-header">
                      <h4>{player.name}</h4>
                      <button onClick={() => removePlayer(player.id)}>‚ùå</button>
                    </div>
                    <p>{player.team} ‚Ä¢ {player.role}</p>
                    <p>‚Ç¨{player.price}M</p>
                  </div>
                ))
              }
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Squad;
