import React, { useState, useEffect } from 'react';
import './Squad.css';

const Squad = () => {
  const [squad, setSquad] = useState([]);
  const [playerInput, setPlayerInput] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  // Limiti per ruolo
  const roleLimits = { GK: 3, DEF: 8, MID: 8, FWD: 6 };
  const countByRole = role => squad.filter(p => p.role === role).length;
  const canAddPlayer = player => countByRole(player.role) < roleLimits[player.role];

  // Funzione di ricerca locale da JSON
  const searchPlayers = (query) => {
    if (!query || query.length < 3) {
      setSearchResults([]);
      return;
    }

    setLoading(true);
    fetch('/players_data.json')
      .then(res => res.json())
      .then(data => {
        const results = data.filter(p =>
          p.name.toLowerCase().includes(query.toLowerCase())
        );
        setSearchResults(results);
      })
      .catch(err => {
        console.error(err);
        setSearchResults([]);
      })
      .finally(() => setLoading(false));
  };

  const addPlayer = (player) => {
    if (!player) return;

    if (!canAddPlayer(player)) {
      alert(`Non puoi aggiungere pi√π ${getRoleName(player.role)}! Limite: ${roleLimits[player.role]}`);
      return;
    }

    const newPlayer = { id: Date.now(), ...player };
    setSquad([...squad, newPlayer]);
    setPlayerInput('');
    setSearchResults([]);
    setSelectedPlayer(null);
  };

  const addFirstPlayer = () => {
    if (searchResults.length > 0) addPlayer(searchResults[0]);
    else if (selectedPlayer) addPlayer(selectedPlayer);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addFirstPlayer();
    }
  };

  const selectPlayer = (player) => setSelectedPlayer(player);

  const removePlayer = (playerId) => {
    setSquad(squad.filter(p => p.id !== playerId));
  };

  const squadByRole = {
    GK: squad.filter(p => p.role === 'GK'),
    DEF: squad.filter(p => p.role === 'DEF'),
    MID: squad.filter(p => p.role === 'MID'),
    FWD: squad.filter(p => p.role === 'FWD')
  };

  const getRoleName = (role) => ({
    GK: 'portieri',
    DEF: 'difensori',
    MID: 'centrocampisti',
    FWD: 'attaccanti'
  }[role] || role);

  // Debounce ricerca
  useEffect(() => {
    const delay = setTimeout(() => {
      if (playerInput.length >= 3) {
        searchPlayers(playerInput);
        setSelectedPlayer(null);
      } else {
        setSearchResults([]);
        setSelectedPlayer(null);
      }
    }, 300);
    return () => clearTimeout(delay);
  }, [playerInput]);

  return (
    <div className="squad-container">
      <h1>üèÜ La Mia Squadra Fantacalcio</h1>

      {/* Riepilogo ruoli */}
      <div className="squad-summary">
        {Object.entries(roleLimits).map(([role, limit]) => {
          const current = countByRole(role);
          return (
            <div key={role} className="role-summary">
              <h4>{getRoleName(role)} ({current}/{limit})</h4>
              <div className="role-progress-bar">
                <div className="role-progress-fill" style={{ width: `${(current / limit) * 100}%` }}></div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Ricerca giocatori */}
      <div className="player-search-section">
        <input
          type="text"
          placeholder="Es: Lautaro Martinez..."
          value={playerInput}
          onChange={e => setPlayerInput(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button 
          onClick={addFirstPlayer}
          disabled={searchResults.length === 0 && !selectedPlayer}
        >
          ‚èé INVIO
        </button>

        {loading && <div className="loading-spinner">üîç Cercando...</div>}

        {searchResults.length > 0 && !selectedPlayer && (
          <div className="search-results">
            {searchResults.map(player => (
              <div
                key={player.id}
                className={`player-card ${!canAddPlayer(player) ? 'disabled' : ''}`}
                onClick={() => canAddPlayer(player) && selectPlayer(player)}
              >
                <h5>{player.name}</h5>
                <p>{player.team} ‚Ä¢ {player.role}</p>
                <p>‚Ç¨{player.price || 0}M</p>
              </div>
            ))}
          </div>
        )}

        {selectedPlayer && (
          <div className="selected-player">
            <h4>üéØ Giocatore selezionato</h4>
            <p>{selectedPlayer.name} ‚Ä¢ {selectedPlayer.team} ‚Ä¢ {selectedPlayer.role}</p>
            <button onClick={() => addPlayer(selectedPlayer)}>‚úÖ Conferma Aggiunta</button>
            <button onClick={() => setSelectedPlayer(null)}>‚ùå Annulla</button>
          </div>
        )}
      </div>

      {/* Rosa completa */}
      <div className="squad-roster">
        {Object.entries(squadByRole).map(([role, players]) => (
          <div key={role} className="role-section">
            <h3>{getRoleName(role)} ({players.length}/{roleLimits[role]})</h3>
            <div className="players-grid">
              {players.length === 0 ? <p>Nessun giocatore</p> :
                players.map(player => (
                  <div key={player.id} className="squad-player-card">
                    <div className="player-header">
                      <h4>{player.name}</h4>
                      <button onClick={() => removePlayer(player.id)}>‚ùå</button>
                    </div>
                    <p>{player.team} ‚Ä¢ {player.role}</p>
                    <p>‚Ç¨{player.price || 0}M</p>
                  </div>
                ))
              }
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Squad;
