import React, { useState, useEffect } from "react";
import "./Squad.css";

const roleLimits = { GK: 3, DEF: 8, MID: 8, FWD: 6 };
const roleNames = { GK: "Portieri", DEF: "Difensori", MID: "Centrocampisti", FWD: "Attaccanti" };

const Squad = ({ currentUser }) => {
  const [playerInput, setPlayerInput] = useState("");
  const [playerData, setPlayerData] = useState(null);
  const [notFound, setNotFound] = useState(false);
  const [loading, setLoading] = useState(false);
  const [squad, setSquad] = useState([]);

  // =======================
  // üîπ FUNZIONI BACKEND
  // =======================
  const fetchSquad = async () => {
    if (!currentUser) return;
    try {
      const res = await fetch(`http://localhost:8000/api/squad/${currentUser}`);
      if (res.ok) {
        const data = await res.json();
        setSquad(data);
      } else setSquad([]);
    } catch (err) {
      console.error("Errore fetch squadra:", err);
      setSquad([]);
    }
  };

  const saveSquad = async (newSquad) => {
    if (!currentUser) return;
    try {
      await fetch(`http://localhost:8000/api/squad/${currentUser}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ squad: newSquad }),
      });
    } catch (err) {
      console.error("Errore save squadra:", err);
    }
  };

  // =======================
  // üîπ CARICA SQUADRA ALL‚ÄôAVVIO O CAMBIO UTENTE
  // =======================
  useEffect(() => {
    fetchSquad();
  }, [currentUser]);

  const normalize = (str) => str.toLowerCase().replace(/\s+/g, " ").trim();

  const cercaGiocatoreEsatto = async () => {
    const nome = normalize(playerInput.trim());
    if (!nome) return;

    setLoading(true);
    setNotFound(false);
    setPlayerData(null);

    try {
      const response = await fetch("http://localhost:8000/api/players/");
      const data = await response.json();

      const trovato = data.players.find((p) => {
        const playerName = normalize(p.name || "");
        const fbrefName = normalize(p.fbref_data?.player || "");
        return playerName === nome || fbrefName === nome;
      });

      if (trovato) setPlayerData(trovato);
      else setNotFound(true);
    } catch (err) {
      console.error("Errore nel caricamento JSON:", err);
      setNotFound(true);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") cercaGiocatoreEsatto();
  };

  const addPlayer = async () => {
    if (!playerData) return;

    const countInRole = squad.filter((p) => p.role === playerData.role).length;
    if (countInRole >= roleLimits[playerData.role]) {
      alert(`Hai gi√† il numero massimo di ${roleNames[playerData.role]}!`);
      return;
    }

    const newSquad = [...squad, playerData];
    setSquad(newSquad);
    await saveSquad(newSquad);

    setPlayerData(null);
    setPlayerInput("");
    setNotFound(false);
  };

  const removePlayer = async (player) => {
    const newSquad = squad.filter(p => p !== player);
    setSquad(newSquad);
    await saveSquad(newSquad);
  };

  const resetSquad = async () => {
    if (!window.confirm("Vuoi davvero resettare la tua squadra?")) return;
    setSquad([]);
    await saveSquad([]);
  };

  const squadByRole = {
    GK: squad.filter((p) => p.role === "GK"),
    DEF: squad.filter((p) => p.role === "DEF"),
    MID: squad.filter((p) => p.role === "MID"),
    FWD: squad.filter((p) => p.role === "FWD"),
  };

  if (!currentUser) return <p>‚ö†Ô∏è Devi effettuare il login per vedere e modificare la tua squadra.</p>;

  return (
    <div className="squad-container">
      <h1>üìä Squadra Fantacalcio</h1>

      <div className="search-section">
        <input
          type="text"
          placeholder="Nome giocatore (es: Sommer o Di Lorenzo)"
          value={playerInput}
          onChange={(e) => setPlayerInput(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button onClick={cercaGiocatoreEsatto}>Cerca</button>
        {playerData && <button onClick={addPlayer}>‚ûï Aggiungi</button>}
      </div>

      {loading && <p>üîÑ Caricamento...</p>}
      {notFound && <p className="notfound">‚ùå Giocatore non trovato</p>}

      {Object.entries(squadByRole).map(([role, players]) => {
        const postiLiberi = roleLimits[role] - players.length;
        if (players.length === 0) return null;

        return (
          <div key={role} className="role-section">
            <h2 data-badge={`${postiLiberi} posti liberi`}>
              {roleNames[role]} ({players.length}/{roleLimits[role]})
            </h2>
            <div className="players-grid">
              {players.map((p) => (
                <div key={p.name + Math.random()} className={`player-card ${p.role}`}>
                  <h3>{p.name}</h3>
                  <p>Ruolo: {p.role} | Squadra: {p.team}</p>
                  <ul>
                    <li>PG: {p.stats.pg}</li>
                    <li>MV: {p.stats.mv}</li>
                    <li>MFV: {p.stats.mfv}</li>
                    <li>GOL: {p.stats.gol}</li>
                    <li>ASS: {p.stats.ass}</li>
                    <li>AMM: {p.stats.amm}</li>
                    <li>ESP: {p.stats.esp}</li>
                    <li>xG: {p.fbref_data?.xg || 0}</li>
                    <li>xA: {p.fbref_data?.xg_assist || 0}</li>
                    <li>Progressivi: {p.fbref_data?.progressive_passes || 0}</li>
                    {p.role === "GK" && (
                      <>
                        <li>Gol subiti: {p.stats.gs}</li>
                        <li>Rigori parati: {p.stats.rp}</li>
                        <li>Clean Sheet: {p.fbref_data?.clean_sheets || 0}</li>
                      </>
                    )}
                  </ul>
                  <p>
                    <a href={p.url} target="_blank" rel="noreferrer">
                      üîó Scheda completa
                    </a>
                  </p>
                  <button className="remove-btn" onClick={() => removePlayer(p)}>‚ùå</button>
                </div>
              ))}
            </div>
          </div>
        );
      })}

      <div className="reset-section">
        <button className="reset-btn" onClick={resetSquad}>üîÑ Resetta squadra</button>
      </div>
    </div>
  );
};

export default Squad;
